    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
      
         String s1 =jList2.getSelectedValue();
        int n =  Integer.parseInt(s1);
        if(jList1.getSelectedValue().equals("BOLD"))
        {
          Font font = new Font("plain", Font.BOLD,n );
         sahilframe.jTextArea1.setFont(font);
        }
        else if(jList1.getSelectedValue().equals("ITALIC"))
        {
          Font font = new Font("plain", Font.ITALIC,n );
         sahilframe.jTextArea1.setFont(font);
        }
        else if(jList1.getSelectedValue().equals("PLAIN"))
        {
          Font font = new Font("plain", Font.PLAIN,n );
         sahilframe.jTextArea1.setFont(font);
        }
        else if(jList1.getSelectedValue().equals("BOLD ITALIC"))
        {
          Font font = new Font("plain",Font.BOLD+Font.ITALIC,n );
         sahilframe.jTextArea1.setFont(font);
        }
        dispose();
        
                // TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

